#!/bin/env python
import os
from Bio.SeqIO.QualityIO import FastqGeneralIterator



input_folder = "/home/ludovic.dutoit/projects/edna_yep/clean_nextera"
output_folder_cephalo = "/home/ludovic.dutoit/projects/edna_yep/clean_nexteracephalo"
output_folder_chordates="/home/ludovic.dutoit/projects/edna_yep/clean_nexterachordates"
file_starts = set([input_folder+"/"+filename .split("L00") [0]for filename in os.listdir("/home/ludovic.dutoit/projects/edna_yep/clean_nextera/")]) 



for filename in file_starts:
	#cephalo primers
	readsonetokeep=[]
	final_primercephalo=[]
	total_counts = 0

	with open(filename+"L001_R1_001_val_1.fq") as in_handle:
	     for title, seq, qual in FastqGeneralIterator(in_handle):
	     	total_counts+=1
	     	if seq.startswith("GACGAG"): readsonetokeep.append(title.split()[0])
	with open(filename+"L001_R2_001_val_2.fq") as in_handle:
	     for title, seq, qual in FastqGeneralIterator(in_handle):
	     	if seq.startswith("AAATTA") and title.split()[0] in readsonetokeep: final_primercephalo.append(title.split()[0])

	#chordates primers
	readsonetokeep=[]
	final_primerchordates=[]
	with open(filename+"L001_R1_001_val_1.fq") as in_handle:
	     for title, seq, qual in FastqGeneralIterator(in_handle):
	     	if seq.startswith("CGAGAA"): readsonetokeep.append(title.split()[0])

	with open(filename+"L001_R2_001_val_2.fq") as in_handle:
	     for title, seq, qual in FastqGeneralIterator(in_handle):
	     	if seq.startswith("TATCCT") and title.split()[0] in readsonetokeep: final_primerchordates.append(title.split()[0])

	#write out
	output1cephalo=open(output_folder_cephalo+"/"+os.path.basename(filename)+"L001_R1_001_val_1.fq","w")
	output2cephalo=open(output_folder_cephalo+"/"+os.path.basename(filename)+"L001_R2_001_val_2.fq","w")
	output1chordates=open(output_folder_chordates+"/"+os.path.basename(filename)+"L001_R1_001_val_1.fq","w")
	output2chordates=open(output_folder_chordates+"/"+os.path.basename(filename)+"L001_R2_001_val_2.fq","w")
	with open(filename+"L001_R1_001_val_1.fq") as in_handle:
	     for title, seq, qual in FastqGeneralIterator(in_handle):
	     	if title.split()[0] in final_primercephalo: output1cephalo.write("\n".join(["@"+ title, seq,"+", qual])+"\n")
	     	if title.split()[0] in final_primerchordates:  output1chordates.write("\n".join([ "@"+title, seq,"+", qual])+"\n")

	with open(filename+"L001_R2_001_val_2.fq") as in_handle:
	     for title, seq, qual in FastqGeneralIterator(in_handle):
 	    	if title.split()[0] in final_primercephalo:
 	    		output2cephalo.write("\n".join([ "@"+title, seq,"+", qual])+"\n")
    	 	if title.split()[0] in final_primerchordates: output2chordates.write("\n".join([ "@"+title, seq,"+", qual])+"\n")
	print(filename,len(final_primerchordates),"chordates",len(final_primercephalo),"cephalo",(len(final_primerchordates)+len(final_primercephalo))/(0.01*float(total_counts)),"%",total_counts)



